import React from "react";
import { Link } from "react-router-dom";

function HeroTestimonials() {
  const darkMode = localStorage.getItem("dark-mode");
  return (
    <section className="relative">
      {/* Background image */}
      <div
        className="absolute inset-0 h-128 pt-16 box-content -z-1"
        style={{ height: "100vh" }}
      >
        <img
          className="absolute inset-0 w-full h-full object-cover dark:opacity-0"
          src={require("../images/darkBg.svg").default}
          alt="Testimonials"
        />
        <img
          className="absolute inset-0 w-full h-full object-cover "
          src={require("../images/darkBg.svg").default}
          alt="Testimonials"
        />
        {/*<div*/}
        {/*  className="absolute inset-0 bg-gradient-to-t from-white dark:from-gray-900"*/}
        {/*  aria-hidden="true"*/}
        {/*/>*/}
      </div>

      <div className="relative max-w-6xl mx-auto px-4 sm:px-6">
        <div className="pt-32 pb-12 md:pt-40 md:pb-20">
          <div className="max-w-3xl mx-auto text-center">
            <h1 className="h1 font-red-hat-display mb-4" data-aos="fade-down">
              EYWA CROSS-CHAIN ECOSYSTEM
            </h1>
            <p
              className="text-xl text-gray-600 dark:text-gray-400"
              data-aos="fade-down"
              data-aos-delay="150"
            >
              powered by EYWA <a>Data Protocol</a>
            </p>
            <div className="mt-8" data-aos="fade-down" data-aos-delay="300">
              <Link
                className="btn text-white bg-teal-500 hover:bg-teal-400 ml-3"
                to="/contact"
              >
                Request code
              </Link>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
}

export default HeroTestimonials;
